% --- Executes on button press in pushbutton4.
function pushbutton4_Callback(hObject, eventdata, handles)
% hObject    handle to pushbutton4 (see GCBO)
% eventdata  reserved - to be defined in a future version of MATLAB
% handles    structure with handles and user data (see GUIDATA)
axes(handles.axes1);
global vid
%vid = videoinput('macvideo', 2,'YUY2_640x480');
vid = videoinput('winvideo', 1);
%set(vid,'ReturnedColorSpace','grayscale');  %灰色图像
set(vid,'ReturnedColorSpace','rgb');  %rgb图像
vidRes=get(vid,'VideoResolution');
width=vidRes(1);
height=vidRes(2);
nBands=get(vid,'NumberOfBands');
hImage = image( zeros(vidRes(2), vidRes(1), nBands) );
preview(vid, hImage);

% --- Executes on button press in pushbutton5.
function pushbutton5_Callback(hObject, eventdata, handles)
% hObject    handle to pushbutton5 (see GCBO)
% eventdata  reserved - to be defined in a future version of MATLAB
% handles    structure with handles and user data (see GUIDATA)
global vid
h=imrect; 
frame=getsnapshot(vid);
pos = getPosition(h); %待区域决定后，运行这句就会返回区域的位置和大小
img=imcrop(frame,pos);
imwrite(frame,'frame.jpg');
imwrite(img,'model.jpg');
str=strcat('x=',num2str(pos(1)+0.5*pos(3)),',y=',num2str(pos(2)+0.5*pos(4)))


%match();

