% --- Executes on button press in pushbutton6.
function pushbutton6_Callback(hObject, eventdata, handles)
% hObject    handle to pushbutton6 (see GCBO)
% eventdata  reserved - to be defined in a future version of MATLAB
% handles    structure with handles and user data (see GUIDATA)
 x=str2num(get(handles.edit1,'string'));%读取笛卡尔空间的数据
 y=str2num(get(handles.edit2,'string'));
 z=str2num(get(handles.edit3,'string'));
theta=DikaerSpace1(x,y,z);
set(handles.edit4,'string',num2str(round(theta(1))));
set(handles.edit5,'string',num2str(round(theta(2))));
set(handles.edit6,'string',num2str(round(theta(3))));
set(handles.edit7,'string',num2str(round(theta(4))));
set(handles.edit8,'string',num2str(round(90)));
set(handles.edit9,'string',num2str(round(90)));

% --- Executes on button press i n pushbutton7.
function pushbutton7_Callback(hObject, eventdata, handles)
% hObject    handle to pushbutton7 (see GCBO)
% eventdata  reserved - to be defined in a future version of MATLAB
% handles    structure with handles and user data (see GUIDATA)
theta(1)=str2num(get(handles.edit4,'string'))-90;%读取关节空间的角度
theta(2)=str2num(get(handles.edit5,'string'))-180;
theta(3)=str2num(get(handles.edit6,'string'))-90;
theta(4)=str2num(get(handles.edit7,'string'))-90;
theta(5)=str2num(get(handles.edit8,'string'))-90;
theta(6)=str2num(get(handles.edit9,'string'))-90;
L(1) = Link([ 0     0       11   -pi/2     0]);%定义连杆
L(2) = Link([ 0     0       85     0        0]);
L(3) = Link([ 0     0       75     0        0]);
L(4) = Link([ 0     0      200    pi/2       0]);
L(5) = Link([0     0       0    -pi/2      0]);
L(6) = Link([ 0     0       0    -pi/2      0]);
bot = SerialLink(L, 'name', 'arm');%连接连杆
forward=bot.fkine([deg2rad(theta)]);
x=forward(1,4);
y=forward(2,4);
z=forward(3,4);
set(handles.edit1,'string',num2str(round(x)));
set(handles.edit2,'string',num2str(round(y)));
set(handles.edit3,'string',num2str(round(z+95)));


